use criterion::{Criterion, criterion_group, criterion_main};
use lazy_static::lazy_static;
use num_complex::Complex;

static SAMPLE_RATE: f32 = 1e5;
static BAUD_RATE: f32 = 1e4;

static BYTE_1: &str = "00000000";
static BYTES_2: &str = "0000000011111111";
static BYTES_4: &str = "00000000111111110000000011111111";
static BYTES_8: &str = "0000000011111111000000001111111100000000111111110000000011111111";
static BYTES_16: &str = "00000000111111110000000011111111000000001111111100000000111111110000000011111111000000001111111100000000111111110000000011111111";
static BYTES_32: &str = "0000000011111111000000001111111100000000111111110000000011111111000000001111111100000000111111110000000011111111000000001111111100000000111111110000000011111111000000001111111100000000111111110000000011111111000000001111111100000000111111110000000011111111";
static BYTES_64: &str = "";
static BYTES_128: &str = "";
static BYTES_256: &str = "";
static BYTES_512: &str = "";
static BYTES_1024: &str = "";
static BYTES_2048: &str = "";


struct BenchData {
    signal_1byte: Vec<Complex<f32>>,
    signal_2bytes: Vec<Complex<f32>>,
    signal_4bytes: Vec<Complex<f32>>,
    signal_8bytes: Vec<Complex<f32>>,
    signal_16bytes: Vec<Complex<f32>>,
    signal_32bytes: Vec<Complex<f32>>,
    signal_64bytes: Vec<Complex<f32>>,
    signal_128bytes: Vec<Complex<f32>>,
    signal_256bytes: Vec<Complex<f32>>,
    signal_512bytes: Vec<Complex<f32>>,
    signal_1024bytes: Vec<Complex<f32>>,
    signal_2048bytes: Vec<Complex<f32>>,
}

impl Default for BenchData {
    fn default() -> Self {
        BenchData {
            signal_1byte: radio::Benchy::mod_ask(BYTE_1, SAMPLE_RATE, BAUD_RATE),
            signal_2bytes: radio::Benchy::mod_ask(BYTES_2, SAMPLE_RATE, BAUD_RATE),
            signal_4bytes: radio::Benchy::mod_ask(BYTES_4, SAMPLE_RATE, BAUD_RATE),
            signal_8bytes: radio::Benchy::mod_ask(BYTES_8, SAMPLE_RATE, BAUD_RATE),
            signal_16bytes: radio::Benchy::mod_ask(BYTES_16, SAMPLE_RATE, BAUD_RATE),
            signal_32bytes: radio::Benchy::mod_ask(BYTES_32, SAMPLE_RATE, BAUD_RATE),
            signal_64bytes: radio::Benchy::mod_ask(BYTES_64, SAMPLE_RATE, BAUD_RATE),
            signal_128bytes: radio::Benchy::mod_ask(BYTES_128, SAMPLE_RATE, BAUD_RATE),
            signal_256bytes: radio::Benchy::mod_ask(BYTES_256, SAMPLE_RATE, BAUD_RATE),
            signal_512bytes: radio::Benchy::mod_ask(BYTES_512, SAMPLE_RATE, BAUD_RATE),
            signal_1024bytes: radio::Benchy::mod_ask(BYTES_1024, SAMPLE_RATE, BAUD_RATE),
            signal_2048bytes: radio::Benchy::mod_ask(BYTES_2048, SAMPLE_RATE, BAUD_RATE),
        }
    }
}

lazy_static! {
    static ref DATA:BenchData = BenchData::default();
}

/// Modulation benchmarks
fn ask_mod_1byte() { radio::Benchy::mod_ask(BYTE_1, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_2bytes() { radio::Benchy::mod_ask(BYTES_2, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_4bytes() { radio::Benchy::mod_ask(BYTES_4, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_8bytes() { radio::Benchy::mod_ask(BYTES_8, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_16bytes() { radio::Benchy::mod_ask(BYTES_16, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_32bytes() { radio::Benchy::mod_ask(BYTES_32, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_64bytes() { radio::Benchy::mod_ask(BYTES_64, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_128bytes() { radio::Benchy::mod_ask(BYTES_128, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_256bytes() { radio::Benchy::mod_ask(BYTES_256, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_512bytes() { radio::Benchy::mod_ask(BYTES_512, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_1024bytes() { radio::Benchy::mod_ask(BYTES_1024, SAMPLE_RATE, BAUD_RATE); }

fn ask_mod_2048bytes() { radio::Benchy::mod_ask(BYTES_2048, SAMPLE_RATE, BAUD_RATE); }


/// Demodulation benchmarks
fn ask_demod_1byte() { radio::Benchy::demod_ask(DATA.signal_1byte.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_2bytes() { radio::Benchy::demod_ask(DATA.signal_2bytes.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_4bytes() { radio::Benchy::demod_ask(DATA.signal_4bytes.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_8bytes() { radio::Benchy::demod_ask(DATA.signal_8bytes.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_16bytes() { radio::Benchy::demod_ask(DATA.signal_16bytes.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_32bytes() { radio::Benchy::demod_ask(DATA.signal_32bytes.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_64bytes() { radio::Benchy::demod_ask(DATA.signal_64bytes.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_128bytes() { radio::Benchy::demod_ask(DATA.signal_128bytes.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_256bytes() { radio::Benchy::demod_ask(DATA.signal_256bytes.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_512bytes() { radio::Benchy::demod_ask(DATA.signal_512bytes.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_1024bytes() { radio::Benchy::demod_ask(DATA.signal_1024bytes.clone(), SAMPLE_RATE, BAUD_RATE); }

fn ask_demod_2048bytes() { radio::Benchy::demod_ask(DATA.signal_2048bytes.clone(), SAMPLE_RATE, BAUD_RATE); }


pub fn criterion_benchmark(c: &mut Criterion) {
    // Modulators benchmarks
    c.bench_function("ASK mod 1 byte", |b| b.iter(ask_mod_1byte));
    c.bench_function("ASK mod 2 bytes", |b| b.iter(ask_mod_2bytes));
    c.bench_function("ASK mod 4 bytes", |b| b.iter(ask_mod_4bytes));
    c.bench_function("ASK mod 8 bytes", |b| b.iter(ask_mod_8bytes));
    c.bench_function("ASK mod 16 bytes", |b| b.iter(ask_mod_16bytes));
    c.bench_function("ASK mod 32 bytes", |b| b.iter(ask_mod_32bytes));
    c.bench_function("ASK mod 64 bytes", |b| b.iter(ask_mod_64bytes));
    c.bench_function("ASK mod 128 bytes", |b| b.iter(ask_mod_128bytes));
    c.bench_function("ASK mod 256 bytes", |b| b.iter(ask_mod_256bytes));
    c.bench_function("ASK mod 512 bytes", |b| b.iter(ask_mod_512bytes));
    c.bench_function("ASK mod 1024 bytes", |b| b.iter(ask_mod_1024bytes));
    c.bench_function("ASK mod 2048 bytes", |b| b.iter(ask_mod_2048bytes));


    // Demodulators benchmarks
    c.bench_function("ASK demod 1 byte", |b| b.iter(ask_mod_1byte));
    c.bench_function("ASK demod 2 bytes", |b| b.iter(ask_mod_2bytes));
    c.bench_function("ASK demod 4 bytes", |b| b.iter(ask_mod_4bytes));
    c.bench_function("ASK demod 8 bytes", |b| b.iter(ask_mod_8bytes));
    c.bench_function("ASK demod 16 bytes", |b| b.iter(ask_mod_16bytes));
    c.bench_function("ASK demod 32 bytes", |b| b.iter(ask_mod_32bytes));
    c.bench_function("ASK demod 64 bytes", |b| b.iter(ask_mod_64bytes));
    c.bench_function("ASK demod 128 bytes", |b| b.iter(ask_mod_128bytes));
    c.bench_function("ASK demod 256 bytes", |b| b.iter(ask_mod_256bytes));
    c.bench_function("ASK demod 512 bytes", |b| b.iter(ask_mod_512bytes));
    c.bench_function("ASK demod 1024 bytes", |b| b.iter(ask_mod_1024bytes));
    c.bench_function("ASK demod 2048 bytes", |b| b.iter(ask_mod_2048bytes));
}


criterion_group!(benches, criterion_benchmark);
criterion_main!(benches);