use criterion::{Criterion, criterion_group, criterion_main};
use lazy_static::lazy_static;
use num_complex::Complex;

use radio::Benchy;

static SAMPLE_RATE: f32 = 1e5;
static BAUD_RATE: f32 = 1e4;

static BYTE_1: &str = "00000000";
static BYTES_2: &str = "0000000011111111";
static BYTES_4: &str = "00000000111111110000000011111111";
static BYTES_8: &str = "0000000011111111000000001111111100000000111111110000000011111111";
static BYTES_16: &str = "00000000111111110000000011111111000000001111111100000000111111110000000011111111000000001111111100000000111111110000000011111111";
static BYTES_32: &str = "0000000011111111000000001111111100000000111111110000000011111111000000001111111100000000111111110000000011111111000000001111111100000000111111110000000011111111000000001111111100000000111111110000000011111111000000001111111100000000111111110000000011111111";
static BYTES_64: &str = "";
static BYTES_128: &str = "";
static BYTES_256: &str = "";
static BYTES_512: &str = "";
static BYTES_1024: &str = "";
static BYTES_2048: &str = "";


#[cfg(test)]
struct BenchData {
    signal_1byte: Vec<Complex<f32>>,
    signal_2bytes: Vec<Complex<f32>>,
    signal_4bytes: Vec<Complex<f32>>,
    signal_8bytes: Vec<Complex<f32>>,
    signal_16bytes: Vec<Complex<f32>>,
    signal_32bytes: Vec<Complex<f32>>,
    signal_64bytes: Vec<Complex<f32>>,
    signal_128bytes: Vec<Complex<f32>>,
    signal_256bytes: Vec<Complex<f32>>,
    signal_512bytes: Vec<Complex<f32>>,
    signal_1024bytes: Vec<Complex<f32>>,
    signal_2048bytes: Vec<Complex<f32>>,
    instance: Benchy,
}

#[cfg(test)]
impl Default for BenchData {
    fn default() -> Self {
        let mut instance = Benchy::new();
        instance.update(SAMPLE_RATE, BAUD_RATE);
        BenchData {
            signal_1byte: instance.mod_bpsk(BYTE_1),
            signal_2bytes: instance.mod_bpsk(BYTES_2),
            signal_4bytes: instance.mod_bpsk(BYTES_4),
            signal_8bytes: instance.mod_bpsk(BYTES_8),
            signal_16bytes: instance.mod_bpsk(BYTES_16),
            signal_32bytes: instance.mod_bpsk(BYTES_32),
            signal_64bytes: instance.mod_bpsk(BYTES_64),
            signal_128bytes: instance.mod_bpsk(BYTES_128),
            signal_256bytes: instance.mod_bpsk(BYTES_256),
            signal_512bytes: instance.mod_bpsk(BYTES_512),
            signal_1024bytes: instance.mod_bpsk(BYTES_1024),
            signal_2048bytes: instance.mod_bpsk(BYTES_2048),
            instance,
        }
    }
}

lazy_static! {
    static ref DATA:BenchData = BenchData::default();
}

/// Modulation benchmarks
fn bpsk_mod_1byte() { DATA.instance.clone().mod_bpsk(BYTE_1); }

fn bpsk_mod_2bytes() { DATA.instance.clone().mod_bpsk(BYTES_2); }

fn bpsk_mod_4bytes() { DATA.instance.clone().mod_bpsk(BYTES_4); }

fn bpsk_mod_8bytes() { DATA.instance.clone().mod_bpsk(BYTES_8); }

fn bpsk_mod_16bytes() { DATA.instance.clone().mod_bpsk(BYTES_16); }

fn bpsk_mod_32bytes() { DATA.instance.clone().mod_bpsk(BYTES_32); }

fn bpsk_mod_64bytes() { DATA.instance.clone().mod_bpsk(BYTES_64); }

fn bpsk_mod_128bytes() { DATA.instance.clone().mod_bpsk(BYTES_128); }

fn bpsk_mod_256bytes() { DATA.instance.clone().mod_bpsk(BYTES_256); }

fn bpsk_mod_512bytes() { DATA.instance.clone().mod_bpsk(BYTES_512); }

fn bpsk_mod_1024bytes() { DATA.instance.clone().mod_bpsk(BYTES_1024); }

fn bpsk_mod_2048bytes() { DATA.instance.clone().mod_bpsk(BYTES_2048); }


/// Demodulation benchmarks
fn bpsk_demod_1byte() { DATA.instance.clone().demod_bpsk(DATA.signal_1byte.clone()); }

fn bpsk_demod_2bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_2bytes.clone()); }

fn bpsk_demod_4bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_4bytes.clone()); }

fn bpsk_demod_8bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_8bytes.clone()); }

fn bpsk_demod_16bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_16bytes.clone()); }

fn bpsk_demod_32bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_32bytes.clone()); }

fn bpsk_demod_64bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_64bytes.clone()); }

fn bpsk_demod_128bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_128bytes.clone()); }

fn bpsk_demod_256bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_256bytes.clone()); }

fn bpsk_demod_512bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_512bytes.clone()); }

fn bpsk_demod_1024bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_1024bytes.clone()); }

fn bpsk_demod_2048bytes() { DATA.instance.clone().demod_bpsk(DATA.signal_2048bytes.clone()); }


pub fn bpsk_benchmark(c: &mut Criterion) {
    // Modulators benchmarks
    c.bench_function("bpsk mod 1 byte", |b| b.iter(bpsk_mod_1byte));
    c.bench_function("bpsk mod 2 bytes", |b| b.iter(bpsk_mod_2bytes));
    c.bench_function("bpsk mod 4 bytes", |b| b.iter(bpsk_mod_4bytes));
    c.bench_function("bpsk mod 8 bytes", |b| b.iter(bpsk_mod_8bytes));
    c.bench_function("bpsk mod 16 bytes", |b| b.iter(bpsk_mod_16bytes));
    c.bench_function("bpsk mod 32 bytes", |b| b.iter(bpsk_mod_32bytes));
    c.bench_function("bpsk mod 64 bytes", |b| b.iter(bpsk_mod_64bytes));
    c.bench_function("bpsk mod 128 bytes", |b| b.iter(bpsk_mod_128bytes));
    c.bench_function("bpsk mod 256 bytes", |b| b.iter(bpsk_mod_256bytes));
    c.bench_function("bpsk mod 512 bytes", |b| b.iter(bpsk_mod_512bytes));
    c.bench_function("bpsk mod 1024 bytes", |b| b.iter(bpsk_mod_1024bytes));
    c.bench_function("bpsk mod 2048 bytes", |b| b.iter(bpsk_mod_2048bytes));


    // Demodulators benchmarks
    c.bench_function("bpsk demod 1 byte", |b| b.iter(bpsk_demod_1byte));
    c.bench_function("bpsk demod 2 bytes", |b| b.iter(bpsk_demod_2bytes));
    c.bench_function("bpsk demod 4 bytes", |b| b.iter(bpsk_demod_4bytes));
    c.bench_function("bpsk demod 8 bytes", |b| b.iter(bpsk_demod_8bytes));
    c.bench_function("bpsk demod 16 bytes", |b| b.iter(bpsk_demod_16bytes));
    c.bench_function("bpsk demod 32 bytes", |b| b.iter(bpsk_demod_32bytes));
    c.bench_function("bpsk demod 64 bytes", |b| b.iter(bpsk_demod_64bytes));
    c.bench_function("bpsk demod 128 bytes", |b| b.iter(bpsk_demod_128bytes));
    c.bench_function("bpsk demod 256 bytes", |b| b.iter(bpsk_demod_256bytes));
    c.bench_function("bpsk demod 512 bytes", |b| b.iter(bpsk_demod_512bytes));
    c.bench_function("bpsk demod 1024 bytes", |b| b.iter(bpsk_demod_1024bytes));
    c.bench_function("bpsk demod 2048 bytes", |b| b.iter(bpsk_demod_2048bytes));
}


criterion_group!(benches, bpsk_benchmark);
criterion_main!(benches);