use lazy_static::lazy_static;
use num_complex::Complex;

#[cfg(test)]
use radio::Testy;

static SAMPLE_RATE: f32 = 256e4;
static BAUD_RATE: f32 = 1e4;

static BYTE_1: &str = "01000101";
static BYTES_2: &str = "1000011001101001";
static BYTES_4: &str = "00110000011000000000110011011101";
static BYTES_8: &str = "1001000101111010100111000100010010010011010011000111110001000000";
static BYTES_16: &str = "11100000111011110001011011010010100110100101101110111100111111110000010100111101100001110000001011000110101111111111000011000011";
static BYTES_32: &str = "1100110001101111000100100100110111111101000110100111001001010000111111011111110001100110001101011101000001011011111010100000001101011010011100000001000001101001000101110101000000111101010110101100011010011001000110100110101101010111110011111000000110110111";
static BYTES_64: &str = "";
static BYTES_128: &str = "";
static BYTES_256: &str = "";
static BYTES_512: &str = "";
static BYTES_1024: &str = "";
static BYTES_2048: &str = "";

#[cfg(test)]
#[derive(Clone)]
struct TestData {
    signal_1byte: Vec<Complex<f32>>,
    signal_2bytes: Vec<Complex<f32>>,
    signal_4bytes: Vec<Complex<f32>>,
    signal_8bytes: Vec<Complex<f32>>,
    signal_16bytes: Vec<Complex<f32>>,
    signal_32bytes: Vec<Complex<f32>>,
    signal_64bytes: Vec<Complex<f32>>,
    signal_128bytes: Vec<Complex<f32>>,
    signal_256bytes: Vec<Complex<f32>>,
    signal_512bytes: Vec<Complex<f32>>,
    signal_1024bytes: Vec<Complex<f32>>,
    signal_2048bytes: Vec<Complex<f32>>,
    instance: Testy,
}

#[cfg(test)]
impl Default for TestData {
    fn default() -> Self {
        let mut instance = Testy::new();
        instance.update(SAMPLE_RATE,BAUD_RATE);
        TestData {
            signal_1byte: instance.mod_mfsk(BYTE_1),
            signal_2bytes: instance.mod_mfsk(BYTES_2),
            signal_4bytes: instance.mod_mfsk(BYTES_4),
            signal_8bytes: instance.mod_mfsk(BYTES_8),
            signal_16bytes: instance.mod_mfsk(BYTES_16),
            signal_32bytes: instance.mod_mfsk(BYTES_32),
            signal_64bytes: instance.mod_mfsk(BYTES_64),
            signal_128bytes: instance.mod_mfsk(BYTES_128),
            signal_256bytes: instance.mod_mfsk(BYTES_256),
            signal_512bytes: instance.mod_mfsk(BYTES_512),
            signal_1024bytes: instance.mod_mfsk(BYTES_1024),
            signal_2048bytes: instance.mod_mfsk(BYTES_2048),
            instance
        }
    }
}

lazy_static! {
    static ref DATA:TestData = TestData::default();
}


#[test]
pub fn mfsk_byte_1() {
    let test = DATA.clone().instance.demod_mfsk(DATA.signal_1byte.clone());

    let expected = BYTE_1;


    assert_eq!(
        test,
        expected,
        "Testing mfsk With 1 Byte of Data.\
            Expected: {}\
            Got: {}",
        expected,
        test
    )
}


